import React, { useEffect , useState} from "react";
import axios from "axios";
import './App.css';
import ima1 from './imagenes/cpu.jpg';
import ima2 from './imagenes/ram.jpg';
import { CircularProgressbar ,buildStyles} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';


var dataResponse=[];

var cpuData=[];
var ramData=[];
var proData=[];

//Indica la maquina que esta seleccionada , por default siempre es la uno 
var Maquina=2;

var direccion='http://127.0.0.1:8080/';

const Consult =async (ruta,body)=>{

  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(body)
};


  const response = await fetch(direccion+ruta,requestOptions)
  if (!response.ok) {
    throw new Error('Data coud not be fetched!')
  } else {
    return response.json()
  }

 /* axios
  .post(direccion+ruta,body)//backend
  .then((res) => {

    //setProducts(res.data);
    console.log("SALE:");
    console.log(res.data);
    dataResponse=res.data
  })
  .catch((err) => {
    console.log("ERROR");
    console.log(err);
  });
*/
}






/*INICIA PARTE GRAFICA EN EL PROGRAMA */

 function App() {
  //const [products, setProducts] = useState(dataResponse);
  const [cpuD, setCpuD] = useState([]);
  const [ramD, setRamD] = useState([]);
  const [proD, setProD] = useState([]);

  const [isLoading1, setIsLoading1] = useState(false);
  const [isLoading2, setIsLoading2] = useState(false);
  const [isLoading3, setIsLoading3] = useState(false);






  const UpdateMachine=()=>{


    //prmera consulta para obtener data del cpu 
    var body1={machine:Maquina}

    useEffect(()=>{
      Consult('get_cpu_info',body1)
      .then((res) => {
        setCpuD(res)
        //cpu=((cpuD["data"][0]["cpu_usado"]/cpuD["data"][0]["cpu_total"])*100)
        setIsLoading1(true);
        console.log(Math.round((cpuD["data"][0]["cpu_usado"]/cpuD["data"][0]["cpu_total"])*100));
      })
      .catch((e) => {
        console.log(e.message)
      });
    }, [])



    //segunda consulta para obtener data de la ram 

    useEffect(()=>{
      Consult('get_ram_info',body1)
      .then((res) => {
        setRamD(res)
        //cpu=((cpuD["data"][0]["cpu_usado"]/cpuD["data"][0]["cpu_total"])*100)
        setIsLoading2(true);
        //console.log(Math.round((ramD["data"][0]["ram_usado"]/ramD["data"][0]["ram_total"])*100));
      })
      .catch((e) => {
        console.log(e.message)
      });
    }, [])



    //segunda consulta para obtener data de los procesos

    useEffect(()=>{
      Consult('get_pro_info',body1)
      .then((res) => {
        setProD(   (res) )
        //cpu=((cpuD["data"][0]["cpu_usado"]/cpuD["data"][0]["cpu_total"])*100)
        setIsLoading3(true);
        //console.log(Math.round((ramD["data"][0]["ram_usado"]/ramD["data"][0]["ram_total"])*100));
      })
      .catch((e) => {
        console.log(e.message)
      });
    }, [])


    console.log( "xxx");
    console.log( cpuD);

    console.log( "yyy");
    console.log( ramD);

    console.log( "zzz");
    console.log( proD);



  }




  UpdateMachine();


  return (

    isLoading1&&isLoading2&&isLoading3 ? (

    <div className="principal" >
    <table class="table table-dark table-striped"  >
      <thead>
        <tr>
            <td colSpan={6}>
              <h4>RENDIMIENTO CPU Y MEMORIA RAM INSTANTANEO</h4>
            </td>
        </tr>
      </thead>
      <thead>
      <tr>
          <td>
            <select class="form-select" onClick={selectMachine} name="maquina" id="maquina">
              <option value="1" selected>Maquina 1</option>
              <option value="2">Maquina 2</option>
              <option value="3">Maquina 3</option>
              <option value="4">Maquina 4</option>
            </select>
          </td>
          <td>
            <button onClick={UpdateMachine}>Actualizar</button>
          </td>

        </tr>
      <tr>
          <td>
            CPU
          </td>
          <td>
            RAM
          </td>

        </tr>
      </thead>
      <tbody>
        <tr>
          <td className="graphs">

            <div className="circle">
              <CircularProgressbar value={Math.round((cpuD["data"][0]["cpu_usado"]/cpuD["data"][0]["cpu_total"])*100)} text={`${Math.round((cpuD["data"][0]["cpu_usado"]/cpuD["data"][0]["cpu_total"])*100)}%`} styles={buildStyles({
                  textColor: "#EEEDED",
                  pathColor: "#0D1282",
                  trailColor: "#EEEDED"
              })}/>

            </div>  

          </td>
          <td className="graphs">

            <div  className="circle">
            <CircularProgressbar value={Math.round((ramD["data"][0]["mem_usado"]/ramD["data"][0]["mem_total"])*100)} text={`${Math.round((ramD["data"][0]["mem_usado"]/ramD["data"][0]["mem_total"])*100)}%`} styles={buildStyles({
                  textColor: "#EEEDED",
                  pathColor: "#0D1282",
                  trailColor: "#EEEDED"
              })}/>

            </div>  

          </td>

        </tr>

        <tr>
          <td>
            total
          </td>
          <td>
            total GB
          </td>
        </tr>





      </tbody>
    </table>






    <table class="table table-dark table-striped">
    <thead>
        <tr>
            <td colSpan={7}>
              <h4>PROCESOS</h4>
            </td>
        </tr>
      </thead>


      <thead>
      <tr>
          <td>
            PARENT_PID
          </td>
          <td>
            PROCESS
          </td>
          <td>
            STATE
          </td>
          <td>
            PIDCHILD
          </td>
          <td>
            PROCESSCHILD
          </td>
          <td>
            STATECHILD
          </td>

        </tr>
      </thead>
      <tbody>

      {
        proD["data"].map((data,index)=>(
          <tr >
            <td>
              <input type="text"  key={index} value={data["PARENT_PID"]} class="form-control"  />
            </td>
            <td>
              <input type="text" value={data["PROCESS"]}   class="form-control"  />
            </td>
            <td>
              <input type="text" value={data["STATE"]}  class="form-control"  />
