import React, { useEffect , useState} from "react";
import axios from "axios";
import './App.css';
import ima1 from './imagenes/cpu.jpg';
import ima2 from './imagenes/ram.jpg';
import { CircularProgressbar ,buildStyles} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';



var datax=[];



//Indica la maquina que esta seleccionada , por default siempre es la uno 
var Maquina=1;

var direccion='http://127.0.0.1:8080/';

async function Consult(ruta,body){
  //const article = { title: 'React POST Request Example' };

  axios
  .post(direccion+ruta,body)//backend
  .then((res) => {
    console.log(res);
    //setProducts(res.data);
    //datax=res.data
  })
  .catch((err) => {
    console.log("adfasd");
    console.log(err);
  });

}



/*INICIA PARTE GRAFICA EN EL PROGRAMA */

async function App() {
  const [products, setProducts] = useState(datax);
  const [valor, setColor] = useState(0);

  /*useEffect(() => {
    loadData();
  }, []);*/






  var cpu=0;

  var ram=0;

  var procesos=[]



  /*
  try{
    procesos=JSON.parse(datax["mensaje"][0]["pro"])

    console.log(procesos[0])
  }catch(es){
    console.log("aun vacio proc")

  }



  try{
    cpu=JSON.parse(datax["mensaje"][0]["cpu"])
    cpu=cpu[0]

    ram=JSON.parse(datax["mensaje"][0]["ram"])
    ram=ram[0]
    console.log(cpu)
    console.log(ram)
  }catch(es){
    console.log("aun vacio proc")

  }

*/


//prmera consulta para obtener data del cpu 
 await Consult('get_cpu_info',body);



  return (
    <div className="principal" >
      <table class="table table-dark table-striped"  >
        <thead>
          <tr>
              <td colSpan={6}>
                <h4>RENDIMIENTO CPU Y MEMORIA RAM INSTANTANEO</h4>
              </td>
          </tr>
        </thead>
        <thead>
        <tr>
            <td>
              <select class="form-select" onClick={selectMachine} name="maquina" id="maquina">
                <option value="1" selected>Maquina 1</option>
                <option value="2">Maquina 2</option>
                <option value="3">Maquina 3</option>
                <option value="4">Maquina 4</option>
              </select>
            </td>
            <td>

            </td>

          </tr>
        <tr>
            <td>
              CPU
            </td>
            <td>
              RAM
            </td>

          </tr>
        </thead>
        <tbody>
          <tr>
            <td className="graphs">

              <div className="circle">
                <CircularProgressbar value={valor} text={`${valor}%`} styles={buildStyles({
                    textColor: "#EEEDED",
                    pathColor: "#0D1282",
                    trailColor: "#EEEDED"
                })}/>
                <button  onClick={() => setColor(valor+1)}>Actualizar </button>
              </div>  

            </td>
            <td className="graphs">

              <div  className="circle">
                <CircularProgressbar value={valor} text={`${valor}%`} styles={buildStyles({
                  textColor: "#EEEDED",
                  pathColor: "#0D1282",
                  trailColor: "#EEEDED"
                })}/>
                <button  onClick={() => setColor(valor+1)}>Actualizar </button>
              </div>  

            </td>

          </tr>

          <tr>
            <td>
              total
            </td>
            <td>
              total GB
            </td>
          </tr>

          <tr>
            <td>
              <input type="text" id="cput" value={cpu["cpu_total"]} class="form-control tamano"   />
            </td>
            <td>
              <input type="text" id="ramt" value={ (ram["mem_total"]/1000000).toFixed(3)}   class="form-control tamano"  />
            </td>
          </tr>

          <tr>
            <td>
              usado %
            </td>
            <td>
              usada %
            </td>
          </tr>

          <tr>
            <td>
              <input type="text" id="cpu" value={(cpu["cpu_usado"]*100/cpu["cpu_total"]).toFixed(3)} class="form-control tamano"   />
            </td>
            <td>
              <input type="text" id="ram" value={(ram["mem_usado"]*100/ram["mem_total"]).toFixed(3)} class="form-control tamano"  />
            </td>
          </tr>
        </tbody>
      </table>






      <table class="table table-dark table-striped">
      <thead>
          <tr>
              <td colSpan={7}>
                <h4>PROCESOS</h4>
              </td>
          </tr>
        </thead>


        <thead>
        <tr>
            <td>
              PARENT_PID
            </td>
            <td>
              PROCESS
            </td>
            <td>
              STATE
            </td>
            <td>
              PIDCHILD
            </td>
            <td>
              PROCESSCHILD
            </td>
            <td>
              STATECHILD
            </td>

          </tr>
        </thead>
        <tbody>

        {
          procesos.map((data,index)=>(
            <tr >
              <td>
                <input type="text"  key={index} value={data["PARENT_PID"]} class="form-control"  />
              </td>
              <td>
                <input type="text" value={data["PROCESS"]}   class="form-control"  />
              </td>
              <td>
                <input type="text" value={data["STATE"]}  class="form-control"  />
              </td>
              <td>
                <input type="text" value={data["PIDCHILD"]}  class="form-control"   />
              </td>
              <td>
                <input type="text" value={data["PROCESSCHILD"]}  class="form-control"  />
              </td>
              <td>
              <input type="text" value={data["STATECHILD"]}  class="form-control"  />
              </td>

            </tr>

          )
          )
        }



        </tbody>
      </table>


    </div>
  );
}

export default App;





function selectMachine(){
  var seleccionado= document.getElementById("maquina").value;


  const body = { machine: Maquina };
  Consult("get_cpu_info",body);

