import React, { useEffect , useState} from "react";
import axios from "axios";
import './App.css';
import ima1 from './imagenes/cpu.jpg';
import ima2 from './imagenes/ram.jpg';
import { CircularProgressbar ,buildStyles} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';


var dataResponse=[];

var cpuData=[];
var ramData=[];
var proData=[];

//Indica la maquina que esta seleccionada , por default siempre es la uno 
var Maquina=1;

var direccion='http://127.0.0.1:8080/';

function Consult(ruta,body){

  axios
  .post(direccion+ruta,body)//backend
  .then((res) => {

    //setProducts(res.data);
    console.log("SALE:");
    console.log(res.data);
    dataResponse=res.data
  })
  .catch((err) => {
    console.log("ERROR");
    console.log(err);
  });

}



/*INICIA PARTE GRAFICA EN EL PROGRAMA */

function App() {
  //const [products, setProducts] = useState(dataResponse);
  const [cpuD, setCpuD] = useState([]);

  (async()=>{






    var CPU=0;

    var RAM=0;

    var procesos=[]



    /*
    try{
      procesos=JSON.parse(datax["mensaje"][0]["pro"])

      console.log(procesos[0])
    }catch(es){
      console.log("aun vacio proc")

    }



    try{
      cpu=JSON.parse(datax["mensaje"][0]["cpu"])
      cpu=cpu[0]

      ram=JSON.parse(datax["mensaje"][0]["ram"])
      ram=ram[0]
      console.log(cpu)
      console.log(ram)
    }catch(es){
      console.log("aun vacio proc")

    }

  */


  //prmera consulta para obtener data del cpu 


      var body1={machine:Maquina}
      await Consult('get_cpu_info',body1);
      setCpuD(dataResponse);

      console.log("fasdfasdf");
      console.log(cpuD);


   //CPU=(cpuData["cpu_usado"]/cpuData["cpu_total"])*100;

   //console.log( cpuData);
  /*
   //if(cpuData.length>0){
    console.log("1:"+cpuData["cpu_usado"]);
    console.log("2:"+cpuData["cpu_total"]);

    console.log("CPU:"+CPU);
   //}

  */ /*
   //segunda consulta para obtener data del ram 
   var body2={machine:Maquina}
   await Consult('get_ram_info',body2);

   //tercera consulta para obtener data del proc 
   var body3={machine:Maquina}
   await Consult('get_pro_info',body3);
  */

    return (
      cpuD.length > 0 ?

      <div className="principal" >
        <table class="table table-dark table-striped"  >
          <thead>
            <tr>
                <td colSpan={6}>
                  <h4>RENDIMIENTO CPU Y MEMORIA RAM INSTANTANEO</h4>
                </td>
            </tr>
          </thead>
          <thead>
          <tr>
              <td>
                <select class="form-select" onClick={selectMachine} name="maquina" id="maquina">
                  <option value="1" selected>Maquina 1</option>
                  <option value="2">Maquina 2</option>
                  <option value="3">Maquina 3</option>
                  <option value="4">Maquina 4</option>
                </select>
              </td>
              <td>

              </td>

            </tr>
          <tr>
              <td>
                CPU
              </td>
              <td>
                RAM
              </td>

            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="graphs">

                <div className="circle">
                  <CircularProgressbar value={((cpuD["data"][0]["cpu_usado"]*100)/cpuD["data"][0]["cpu_total"])} text={`${((cpuD["data"][0]["cpu_usado"]*100)/cpuD["data"][0]["cpu_total"])}%`} styles={buildStyles({
                      textColor: "#EEEDED",
                      pathColor: "#0D1282",
                      trailColor: "#EEEDED"
                  })}/>

                </div>  

              </td>
              <td className="graphs">

                <div  className="circle">
                  <CircularProgressbar value={((cpuD["data"][0]["cpu_usado"]*100)/cpuD["data"][0]["cpu_total"])} text={`${(cpuD["data"][0]["cpu_usado"]*100)/cpuD["data"][0]["cpu_total"]}%`} styles={buildStyles({
                    textColor: "#EEEDED",
                    pathColor: "#0D1282",
                    trailColor: "#EEEDED"
                  })}/>

                </div>  

              </td>

            </tr>

            <tr>
              <td>
                total
              </td>
              <td>
                total GB
              </td>
            </tr>





          </tbody>
        </table>






        <table class="table table-dark table-striped">
        <thead>
            <tr>
                <td colSpan={7}>
                  <h4>PROCESOS</h4>
                </td>
            </tr>
          </thead>


          <thead>
          <tr>
              <td>
                PARENT_PID
              </td>
              <td>
                PROCESS
              </td>
              <td>
                STATE
              </td>
              <td>
                PIDCHILD
              </td>
              <td>
                PROCESSCHILD
              </td>
              <td>
                STATECHILD
              </td>

            </tr>
          </thead>
          <tbody>

          {
            procesos.map((data,index)=>(
              <tr >
                <td>
                  <input type="text"  key={index} value={data["PARENT_PID"]} class="form-control"  />
                </td>
                <td>
                  <input type="text" value={data["PROCESS"]}   class="form-control"  />
                </td>
                <td>
                  <input type="text" value={data["STATE"]}  class="form-control"  />
                </td>
                <td>
                  <input type="text" value={data["PIDCHILD"]}  class="form-control"   />
                </td>
                <td>
                  <input type="text" value={data["PROCESSCHILD"]}  class="form-control"  />
                </td>
                <td>
                <input type="text" value={data["STATECHILD"]}  class="form-control"  />
                </td>

              </tr>

            )
            )
          }



          </tbody>
        </table>


      </div>


      : 
      null


    );




})();




}

export default App;





function selectMachine(){
  var seleccionado= document.getElementById("maquina").value;


  const body = { machine: Maquina };
  Consult("get_cpu_info",body);

  try{
    console.log(seleccionado);
    Maquina=parseInt(seleccionado);
  }catch(error){

  }
}
