import React, { useEffect , useState} from "react";
import { Link ,useNavigate } from "react-router-dom";
import './App.css';
import { CircularProgressbar ,buildStyles} from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';


import CanvasJSReact from '@canvasjs/react-charts';
//var CanvasJSReact = require('@canvasjs/react-charts');
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;





//Indica la maquina que esta seleccionada , por default siempre es la uno 
var Maquina='0';

var direccion='http://127.0.0.1:8080/';




const Consult =async (ruta,body)=>{

  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(body)
  };

  const response = await fetch(direccion+ruta,requestOptions)
  if (!response.ok) {
    throw new Error('Data coud not be fetched!')
  } else {
    return response.json()
  }

}







/*INICIA PARTE GRAFICA EN EL PROGRAMA */

 function Graficas() {
   //const [products, setProducts] = useState(dataResponse);
   const [cpuD, setCpuD] = useState([]);
   const [ramD, setRamD] = useState([]);
   const [proD, setProD] = useState([]);
   const [macD, setMacD] = useState([]);

   const [isLoading1, setIsLoading1] = useState(false);
   const [isLoading2, setIsLoading2] = useState(false);
   const [isLoading3, setIsLoading3] = useState(false);

   const [isLoading4, setIsLoading4] = useState(false);

   const navigate = useNavigate();



   const inicioH = () => {
     // 👇️ navigate to /
     navigate('/inicio');
   };






   const UpdateData=()=>{

    if(Maquina!=='0'){
      //prmera consulta para obtener data del cpu 
      var body1={machine:Maquina}

      Consult('get_cpu_values',body1)
      .then((res) => {
        var cpu=[]

        for (let i = 0; i < res["data"].length; i++) {
          const element = res["data"][i];
          var calculo=parseFloat(((element["cpu_usado"]/element["cpu_total"])*100).toFixed(4));
          cpu.push( { x: i, y: calculo})
        }
        setCpuD(cpu)
        setIsLoading1(true);

      })
      .catch((e) => {
        console.log(e.message)
      });

/////////////


      Consult('get_ram_values',body1)
      .then((res) => {
        var ram=[]

        for (let i = 0; i < res["data"].length; i++) {
          const element = res["data"][i];
          var calculo=parseFloat(((element["mem_usado"]/element["mem_total"])*100).toFixed(4));
          ram.push( { x: i, y: calculo})


        }
        setRamD(ram)
        setIsLoading2(true);

      })
      .catch((e) => {
        console.log(e.message)
      });



        console.log( "xxx");
        console.log( cpuD);

        console.log( "yyy");
        console.log( ramD);



        console.log( "maquina");
        console.log( Maquina);
    }

   }



   const GetMachines = () => {
     var body1={machine:""}
     Consult('get_machines',body1)
     .then((res) => {
       const datan=res;
       console.log("aaa");
       console.log(datan);
       setMacD(datan);
       setIsLoading4(true);
       console.log("asigna");
       console.log(macD);

     })
     .catch((e) => {
       console.log(e.message)
     });
   };



     /*
     useEffect(()=>{
       UpdateData();
     }, [])
     */

     useEffect(()=>{
       GetMachines() ;

       //if(!isLoading4){
       //}
     }, [setMacD])






 ///////////////////////////////////
 console.log("ddddddddddddddddd");
console.log(cpuD);

 const options = {
  animationEnabled: true,
  theme: "light2",
  data: [{
    type: "spline",
    showInLegend: false,
    dataPoints: cpuD
  }]
}

const options2 = {
  animationEnabled: true,
  theme: "light2",
  data: [{
    type: "spline",
    showInLegend: false,
    dataPoints: ramD
  }]
}

 //////////////////////////////////////
   return (

     isLoading4 ? (

     <div className="principal" >

     <table class="table table-dark table-striped"  >
       <thead>
         <tr>
             <td colSpan={6}>
               <h4>RENDIMIENTO CPU Y MEMORIA RAM GRAFICA</h4>
             </td>
         </tr>
       </thead>
       <thead>
       <tr>
           <td>
             <select class="form-select" onClick={selectMachine} name="maquina" id="maquina">
                  <option value="0" selected>Seleccione la maquina </option>
                 {
                   macD["data"].map((data,index)=>(
                     <option value={data["machine"]}>Maquina {data["machine"]}</option>
                   ))
                 }


             </select>
           </td>
           <td>
             <button onClick={UpdateData}>Actualizar</button>
             &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
             <button onClick={inicioH}>Inicio</button>
           </td>

         </tr>
       <tr>
           <td>
             CPU
           </td>
           <td>
             RAM
           </td>

         </tr>
       </thead>



       {

         isLoading1&&isLoading2 ? (
           <tbody>
             <tr>
               <td className="graphs">

                 <div className="cuadro">
                  <CanvasJSChart className="grafica" options = {options} />

                 </div>  

               </td>
               <td className="graphs">

                 <div  className="cuadro">
                  <CanvasJSChart className="grafica" options = {options} />

                 </div>  

               </td>

             </tr>

             <tr>
               <td>
                 total
               </td>
               <td>
                 total GB
               </td>
             </tr>
           </tbody>
         ) : (

             <tbody>

             </tbody>

         )


       }

     </table>







     {

       isLoading3 ? (
         <table class="table table-dark table-striped">
         <thead>
             <tr>
                 <td colSpan={7}>
                   <h4>PROCESOS</h4>
                 </td>
             </tr>
           </thead>


           <thead>
           <tr>
               <td>
                 PARENT_PID
               </td>
               <td>
                 PROCESS
               </td>
